using AdventOfCode.Util;
using AdventOfCode.Year2024;

namespace AdventOfCodeTests.Year2024
{
    public class Day15Tests
    {
        [Test]
        public void WarehouseRobotPredictor_SumGpsForMap_ShouldCalculateGpsForMap()
        {
            WarehouseRobotPredictor.SumGpsForMap(new Map("#######\r\n#...O..\r\n#......")).Should().Be(104);
        }

        [Test]
        public void WarehouseRobotPredictor_SumGpsCoords_ShouldSolveSmallTestInput()
        {
            WarehouseRobotPredictor.SumGpsCoords(_smallTestInput).Should().Be(2028);
        }

        [Test]
        public void WarehouseRobotPredictor_SumGpsCoords_ShouldSolveTestInput()
        {
            WarehouseRobotPredictor.SumGpsCoords(_puzzleTestInput).Should().Be(10092);
        }

        [Test]
        public void WarehouseRobotPredictor_SumGpsCoords_ShouldSolveInput()
        {
            WarehouseRobotPredictor.SumGpsCoords(_puzzleInput).Should().Be(1490942);
        }

        [Test]
        public void WarehouseRobotPredictor_SumGpsForMap_ShouldCalculateGpsForMapPart2()
        {
            WarehouseRobotPredictor.SumGpsForMap(new Map("##########\r\n##...[]...\r\n##........")).Should().Be(105);
        }

        [Test]
        public void WarehouseRobotPredictor_SumGpsCoords_ShouldSolveTestInputPart2()
        {
            WarehouseRobotPredictor.SumGpsCoordsInWideMap(_puzzleTestInput).Should().Be(9021);
        }

        [Test]
        public void WarehouseRobotPredictor_SumGpsCoords_ShouldSolveInputPart2()
        {
            WarehouseRobotPredictor.SumGpsCoordsInWideMap(_puzzleInput).Should().Be(1519202);
        }

